var a,b,c,delta=0,x1=0,x2=0;function checkCalc(){a=document.getElementById("a").value,b=document.getElementById("b").value,c=document.getElementById("c").value,""==a||""==b||""==c?alert("Please declare all variables (a, b, c)!"):0==a?alert("variable 'a' cannot be declared as '0'"):calculate()}function clearAll(){a=0,b=0,c=0,delta=0,x1=0,x2=0,document.getElementById("a").value="",document.getElementById("b").value="",document.getElementById("c").value="",document.getElementById("result").innerHTML="x = ?"}function calculate(){(delta=Math.pow(b,2)+-4*a*c)>0?(x1=(-b+Math.sqrt(delta))/(2*a),x2=(-b-Math.sqrt(delta))/(2*a),document.getElementById("result").innerHTML="x1 = "+x1+" , x2 = "+x2):0==delta?(x1=-b/(2*a),document.getElementById("result").innerHTML="x = "+x1):document.getElementById("result").innerHTML="x = no real value"}document.onkeydown=function(e){"Enter"==e.key&&checkCalc()};
